import { Box, IconButton, Typography, Chip } from '@mui/material';
import { DataGrid } from '@mui/x-data-grid';
import { Check as CheckIcon } from '@mui/icons-material';

const OrderList = ({ orders, onConfirm }) => {
  const columns = [
    { 
      field: 'createdAt', 
      headerName: 'Ngày đặt', 
      width: 180,
      valueFormatter: (params) => {
        return new Date(params.value).toLocaleString('vi-VN');
      }
    },
    { field: 'shippingInfo.fullName', headerName: 'Tên khách hàng', width: 150 },
    { field: 'shippingInfo.phone', headerName: 'SĐT', width: 120 },
    { 
      field: 'shippingInfo.address', 
      headerName: 'Địa chỉ', 
      width: 200,
      valueGetter: (params) => {
        const info = params.row.shippingInfo;
        return `${info.address}, ${info.ward}, ${info.district}, ${info.city}`;
      }
    },
    { 
      field: 'items', 
      headerName: 'Sản phẩm', 
      width: 250,
      renderCell: (params) => (
        <div>
          {params.value.map((item, index) => (
            <Typography key={`${item.productId}-${index}`} variant="body2" sx={{ mb: 0.5 }}>
              {item.name} (x{item.quantity}) - Size: {item.size}
            </Typography>
          ))}
        </div>
      )
    },
    { 
      field: 'totalAmount', 
      headerName: 'Tổng tiền', 
      width: 130,
      renderCell: (params) => (
        <Typography variant="body2">
          {new Intl.NumberFormat('vi-VN', { style: 'currency', currency: 'VND' }).format(params.value)}
        </Typography>
      )
    },
    { 
      field: 'paymentMethod', 
      headerName: 'Thanh toán', 
      width: 120,
      renderCell: (params) => (
        <Chip 
          label={params.value === 'cod' ? 'Tiền mặt' : 'Online'} 
          color={params.value === 'cod' ? 'default' : 'info'}
          size="small"
        />
      )
    },
    { 
      field: 'paymentStatus', 
      headerName: 'TT Thanh toán', 
      width: 130,
      renderCell: (params) => (
        <Chip 
          label={params.value === 'pending' ? 'Chờ thanh toán' : 'Đã thanh toán'} 
          color={params.value === 'pending' ? 'warning' : 'success'}
          size="small"
        />
      )
    },
    { 
      field: 'status', 
      headerName: 'Trạng thái', 
      width: 130,
      renderCell: (params) => (
        <Chip 
          label={
            params.value === 'pending' ? 'Chờ xác nhận' : 
            params.value === 'confirmed' ? 'Đã xác nhận' :
            params.value === 'completed' ? 'Hoàn thành' : 'Đã hủy'
          }
          color={
            params.value === 'pending' ? 'warning' :
            params.value === 'confirmed' ? 'info' :
            params.value === 'completed' ? 'success' : 'error'
          }
          size="small"
        />
      )
    },
    {
      field: 'actions',
      headerName: 'Thao tác',
      width: 100,
      renderCell: (params) => (
        params.row.status === 'pending' && (
          <IconButton 
            color="primary" 
            onClick={() => onConfirm(params.row._id)}
            title="Xác nhận đơn hàng"
          >
            <CheckIcon />
          </IconButton>
        )
      )
    }
  ];

  return (
    <DataGrid
      rows={orders}
      columns={columns}
      pageSize={10}
      rowsPerPageOptions={[10]}
      getRowId={(row) => row._id}
      autoHeight
      disableSelectionOnClick
      loading={orders.length === 0}
      initialState={{
        sorting: {
          sortModel: [{ field: 'createdAt', sort: 'desc' }],
        },
      }}
    />
  );
};

export default OrderList;
