import {
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  Button,
  TextField,
  MenuItem
} from '@mui/material';

const OrderForm = ({ open, onClose, formData, setFormData, onSubmit }) => {
  const statusOptions = [
    { value: 'pending', label: 'Chờ xác nhận' },
    { value: 'confirmed', label: 'Đã xác nhận' },
    { value: 'shipping', label: 'Đang giao' },
    { value: 'delivered', label: 'Đã giao' },
    { value: 'cancelled', label: 'Đã hủy' }
  ];

  const paymentMethods = [
    { value: 'cod', label: 'Tiền mặt' },
    { value: 'banking', label: 'Chuyển khoản' },
    { value: 'vnpay', label: 'VNPay' }
  ];

  return (
    <Dialog open={open} onClose={onClose} maxWidth="md" fullWidth>
      <DialogTitle>
        {formData._id ? 'Sửa đơn hàng' : 'Thêm đơn hàng'}
      </DialogTitle>
      <DialogContent>
        <TextField
          autoFocus
          margin="dense"
          label="Tên khách hàng"
          fullWidth
          value={formData.shippingInfo.fullName}
          onChange={(e) => setFormData({ 
            ...formData, 
            shippingInfo: { ...formData.shippingInfo, fullName: e.target.value } 
          })}
        />
        {/* ... other shipping fields ... */}
        <TextField
          margin="dense"
          label="Tổng tiền"
          type="number"
          fullWidth
          value={formData.totalAmount}
          onChange={(e) => setFormData({ ...formData, totalAmount: e.target.value })}
        />
        <TextField
          select
          margin="dense"
          label="Trạng thái"
          fullWidth
          value={formData.status}
          onChange={(e) => setFormData({ ...formData, status: e.target.value })}
        >
          {statusOptions.map((option) => (
            <MenuItem key={option.value} value={option.value}>
              {option.label}
            </MenuItem>
          ))}
        </TextField>
        <TextField
          select
          margin="dense"
          label="Phương thức thanh toán"
          fullWidth
          value={formData.paymentMethod}
          onChange={(e) => setFormData({ ...formData, paymentMethod: e.target.value })}
        >
          {paymentMethods.map((option) => (
            <MenuItem key={option.value} value={option.value}>
              {option.label}
            </MenuItem>
          ))}
        </TextField>
      </DialogContent>
      <DialogActions>
        <Button onClick={onClose}>Hủy</Button>
        <Button onClick={onSubmit} variant="contained" color="primary">
          {formData._id ? 'Cập nhật' : 'Thêm'}
        </Button>
      </DialogActions>
    </Dialog>
  );
};

export default OrderForm;
