import { Box, IconButton, Typography } from '@mui/material';
import { DataGrid } from '@mui/x-data-grid';
import { Check as CheckIcon } from '@mui/icons-material';

const OrderList = ({ orders, onConfirm }) => {
  const columns = [
    { field: 'shippingInfo.fullName', headerName: 'Tên khách hàng', width: 200 },
    { field: 'shippingInfo.email', headerName: 'Email', width: 200 },
    { field: 'shippingInfo.phone', headerName: 'Điện thoại', width: 150 },
    { field: 'shippingInfo.address', headerName: 'Địa chỉ', width: 250 },
    {
      field: 'items',
      headerName: 'Sản phẩm',
      width: 300,
      renderCell: (params) => (
        <div>
          {params.value.map((item) => (
            <div key={`${item.productId._id}-${item.size}`}>
              <Typography variant="body2">
                {item.name} (x{item.quantity}) - {new Intl.NumberFormat('vi-VN', { style: 'currency', currency: 'VND' }).format(item.price)} - Kích cỡ: {Array.isArray(item.size) ? item.size.join(', ') : item.size}
              </Typography>
            </div>
          ))}
        </div>
      )
    },
    { 
      field: 'totalAmount', 
      headerName: 'Tổng tiền', 
      width: 150, 
      renderCell: (params) => (
        <Typography variant="body2">
          {new Intl.NumberFormat('vi-VN', { style: 'currency', currency: 'VND' }).format(params.value)}
        </Typography>
      )
    },
    { field: 'status', headerName: 'Trạng thái', width: 150 },
    { field: 'paymentMethod', headerName: 'Phương thức thanh toán', width: 150 },
    { field: 'paymentStatus', headerName: 'Trạng thái thanh toán', width: 150 },
    {
      field: 'actions',
      headerName: 'Thao tác',
      width: 130,
      renderCell: (params) => (
        <Box>
          <IconButton color="primary" onClick={() => onConfirm(params.row._id)}>
            <CheckIcon />
          </IconButton>
        </Box>
      )
    }
  ];

  return (
    <DataGrid
      rows={orders}
      columns={columns}
      pageSize={10}
      rowsPerPageOptions={[10]}
      getRowId={(row) => row._id}
      autoHeight
      disableSelectionOnClick
      loading={orders.length === 0}
    />
  );
};

export default OrderList;
