import {
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  Button,
  TextField,
} from '@mui/material';

const OrderForm = ({ open, onClose, formData, setFormData, onSubmit }) => {
  return (
    <Dialog open={open} onClose={onClose}>
      <DialogContent>
        <TextField
          autoFocus
          margin="dense"
          label="Tên khách hàng"
          fullWidth
          value={formData.shippingInfo.fullName}
          onChange={(e) => setFormData({ ...formData, shippingInfo: { ...formData.shippingInfo, fullName: e.target.value } })}
        />
        <TextField
          margin="dense"
          label="Email"
          fullWidth
          value={formData.shippingInfo.email}
          onChange={(e) => setFormData({ ...formData, shippingInfo: { ...formData.shippingInfo, email: e.target.value } })}
        />
        <TextField
          margin="dense"
          label="Số điện thoại"
          fullWidth
          value={formData.shippingInfo.phone}
          onChange={(e) => setFormData({ ...formData, shippingInfo: { ...formData.shippingInfo, phone: e.target.value } })}
        />
        <TextField
          margin="dense"
          label="Địa chỉ"
          fullWidth
          value={formData.shippingInfo.address}
          onChange={(e) => setFormData({ ...formData, shippingInfo: { ...formData.shippingInfo, address: e.target.value } })}
        />
        <TextField
          margin="dense"
          label="Thành phố"
          fullWidth
          value={formData.shippingInfo.city}
          onChange={(e) => setFormData({ ...formData, shippingInfo: { ...formData.shippingInfo, city: e.target.value } })}
        />
        <TextField
          margin="dense"
          label="Quận/Huyện"
          fullWidth
          value={formData.shippingInfo.district}
          onChange={(e) => setFormData({ ...formData, shippingInfo: { ...formData.shippingInfo, district: e.target.value } })}
        />
        <TextField
          margin="dense"
          label="Phường/Xã"
          fullWidth
          value={formData.shippingInfo.ward}
          onChange={(e) => setFormData({ ...formData, shippingInfo: { ...formData.shippingInfo, ward: e.target.value } })}
        />
        <TextField
          margin="dense"
          label="Ghi chú"
          fullWidth
          value={formData.shippingInfo.note}
          onChange={(e) => setFormData({ ...formData, shippingInfo: { ...formData.shippingInfo, note: e.target.value } })}
        />
        <TextField
          margin="dense"
          label="Tổng tiền"
          type="number"
          fullWidth
          value={formData.totalAmount}
          onChange={(e) => setFormData({ ...formData, totalAmount: e.target.value })}
        />
        <TextField
          margin="dense"
          label="Trạng thái"
          fullWidth
          value={formData.status}
          onChange={(e) => setFormData({ ...formData, status: e.target.value })}
        />
        <TextField
          margin="dense"
          label="Phương thức thanh toán"
          fullWidth
          value={formData.paymentMethod}
          onChange={(e) => setFormData({ ...formData, paymentMethod: e.target.value })}
        />
      </DialogContent>
      <DialogActions>
        <Button onClick={onClose}>Hủy</Button>
        <Button onClick={onSubmit} variant="contained">
          {formData._id ? 'Cập nhật' : 'Thêm'}
        </Button>
      </DialogActions>
    </Dialog>
  );
};

export default OrderForm;
