import { useState, useEffect } from 'react';
import { Box, Button, Snackbar, Alert } from '@mui/material';
import axios from 'axios';
import OrderList from '../components/orders/OrderList';
import OrderForm from '../components/orders/OrderForm';

const OrdersPage = () => {
  const [orders, setOrders] = useState([]);
  const [open, setOpen] = useState(false);
  const [formData, setFormData] = useState({
    customerName: '',
    product: '',
    quantity: '',
    status: ''
  });
  const [editId, setEditId] = useState(null);
  const [error, setError] = useState('');
  const [snackbar, setSnackbar] = useState({ open: false, message: '', severity: 'success' });

  const fetchOrders = async () => {
    try {
      const headers = {
        Authorization: `Bearer ${localStorage.getItem('token')}`
      };
      const response = await axios.get('http://localhost:5000/api/orders', { headers });
      setOrders(response.data);
    } catch (error) {
      console.error('Error fetching orders:', error);
      setError('Không thể tải danh sách đơn hàng');
    }
  };

  useEffect(() => {
    fetchOrders();
  }, []);

  const handleSubmit = async () => {
    try {
      const token = localStorage.getItem('token');
      if (!token) {
        setError('Không tìm thấy token xác thực. Vui lòng đăng nhập lại.');
        return;
      }

      const config = {
        headers: {
          Authorization: `Bearer ${token}`,
          'Content-Type': 'application/json'
        }
      };

      if (editId) {
        await axios.put(`http://localhost:5000/api/orders/${editId}`, formData, config);
        setSnackbar({ open: true, message: 'Cập nhật đơn hàng thành công', severity: 'success' });
      } else {
        await axios.post('http://localhost:5000/api/orders', formData, config);
        setSnackbar({ open: true, message: 'Thêm đơn hàng thành công', severity: 'success' });
      }

      fetchOrders();
      handleClose();
    } catch (error) {
      console.error('Error saving order:', error);
      setError('Có lỗi xảy ra khi lưu đơn hàng');
      setSnackbar({ open: true, message: 'Có lỗi xảy ra khi lưu đơn hàng', severity: 'error' });
    }
  };

  const handleEdit = (order) => {
    setFormData({
      customerName: order.customerName || '',
      product: order.product || '',
      quantity: order.quantity || '',
      status: order.status || ''
    });
    setEditId(order._id);
    setOpen(true);
  };

  const handleDelete = async (id) => {
    if (window.confirm('Bạn có chắc muốn xóa đơn hàng này?')) {
      try {
        const headers = {
          Authorization: `Bearer ${localStorage.getItem('token')}`
        };
        await axios.delete(`http://localhost:5000/api/orders/${id}`, { headers });
        fetchOrders();
      } catch (error) {
        console.error('Error deleting order:', error);
        setError('Không thể xóa đơn hàng');
      }
    }
  };

  const handleClose = () => {
    setOpen(false);
    setFormData({
      customerName: '',
      product: '',
      quantity: '',
      status: ''
    });
    setEditId(null);
    setError('');
  };

  return (
    <Box>
      <Button variant="contained" onClick={() => setOpen(true)}>
        Thêm đơn hàng
      </Button>

      <OrderList 
        orders={orders}
        onEdit={handleEdit}
        onDelete={handleDelete}
      />

      <OrderForm
        open={open}
        onClose={handleClose}
        formData={formData}
        setFormData={setFormData}
        onSubmit={handleSubmit}
        editId={editId}
      />

      <Snackbar 
        open={!!error || snackbar.open} 
        autoHideDuration={6000} 
        onClose={() => setError('')}
      >
        <Alert severity={snackbar.severity || 'error'} onClose={() => setError('')}>
          {error || snackbar.message}
        </Alert>
      </Snackbar>
    </Box>
  );
};

export default OrdersPage;
