import { useState, useEffect } from 'react';
import { Box, Snackbar, Alert } from '@mui/material';
import axios from 'axios';
import OrderList from '../components/orders/OrderList';
import OrderForm from '../components/orders/OrderForm';
import { AlarmOff } from '@mui/icons-material';

const OrdersPage = () => {
  const [orders, setOrders] = useState([]);
  const [error, setError] = useState('');
  const [snackbar, setSnackbar] = useState({ open: false, message: '', severity: 'success' });
  const [formData, setFormData] = useState({
    fullName: '',
    email: '',
    phone: '',
    address: '',
    totalAmount: 0,
    status: '',
    paymentMethod: '',
    items: []
  });
  const [open, setOpen] = useState(false);

    const fetchOrders = async () => {
      try {
        const headers = {
          Authorization: `Bearer ${localStorage.getItem('token')}`
        };
        const response = await axios.get('http://localhost:5000/api/orders/admin/all', { headers });

        const formattedOrders = response.data.orders.map(order => ({
          _id: order._id,
          user: order.user.map(user => ({
            fullName: user.fullName,
            email: user.email,
            phone: user.phone,
            address: user.address
          })),
          items: order.items.map(item => ({
            productId: item.productId._id,
            name: item.name,
            quantity: item.quantity,
            price: item.price,
            size: Array.isArray(item.size) ? item.size : [item.size]
          })),
          fullName: order.shippingInfo.fullName, // Trường fullName
          email: order.shippingInfo.email,       // Trường email
          phone: order.shippingInfo.phone,       // Trường phone
          address: order.shippingInfo.address,   // Trường address
          totalAmount: order.totalAmount,
          status: order.status,
          paymentMethod: order.paymentMethod,
          paymentStatus: order.paymentStatus,
          createdAt: order.createdAt,
          updatedAt: order.updatedAt
        }));

        setOrders(formattedOrders);
      } catch (error) {
        console.error('Error fetching orders:', error);
        setError('Không thể tải danh sách đơn hàng');
      }
    };

  useEffect(() => {
    fetchOrders();
  }, []);

  const validStatuses = ['pending', 'processing', 'completed', 'cancelled']; // Updated valid statuses
  const handleConfirm = async (id) => {
    try {
      const headers = {
        Authorization: `Bearer ${localStorage.getItem('token')}`
      };

      // Check if 'completed' is a valid status
      if (!validStatuses.includes('completed')) {
        throw new Error('Invalid status value: completed');
      }

      const response = await axios.put(
        `http://localhost:5000/api/orders/${id}/status`, 
        { 
          status: 'completed', // Use 'completed' as the status
          updatedAt: new Date().toISOString() // Add timestamp
        }, 
        { headers }
      );

      if (response.data) {
        setSnackbar({ 
          open: true, 
          message: 'Xác nhận đơn hàng thành công', 
          severity: 'success' 
        });
        fetchOrders();
      }
    } catch (error) {
      console.error('Error confirming order:', error.response || error);
      const errorMessage = error.response?.data?.message 
        || error.response?.data?.error 
        || 'Có lỗi xảy ra khi xác nhận đơn hàng. Vui lòng thử lại sau.';
      setSnackbar({ 
        open: true, 
        message: errorMessage, 
        severity: 'error' 
      });
    }
  };

const handleEditOrder = (order) => {
  setFormData({
    fullName: order.shippingInfo.fullName,
    email: order.shippingInfo.email,
    phone: order.shippingInfo.phone,
    address: order.shippingInfo.address,
    totalAmount: order.totalAmount,
    status: order.status,
    paymentMethod: order.paymentMethod,
    items: order.items
  });
  setOpen(true);
};

  return (
    <Box>
      <OrderList orders={orders} onConfirm={handleConfirm} />
      <OrderForm open={open} onClose={() => setOpen(false)} formData={formData} setFormData={setFormData} onSubmit={handleEditOrder} />
      <Snackbar open={!!error || snackbar.open} autoHideDuration={6000} onClose={() => setError('')}>
        <Alert severity={snackbar.severity || 'error'} onClose={() => setError('')}>
          {error || snackbar.message}
        </Alert>
      </Snackbar>
    </Box>
  );
};

export default OrdersPage;
