import { DataGrid } from '@mui/x-data-grid';
import { Box, IconButton } from '@mui/material';
import CheckIcon from '@mui/icons-material/Check';
import EditIcon from '@mui/icons-material/Edit';

const OrderList = ({ orders, onConfirm, onEdit }) => {
  const columns = [
    { field: '_id', headerName: 'ID', width: 220 },
    { 
      field: 'shippingInfo', 
      headerName: 'Tên khách hàng', 
      width: 200,
      valueGetter: (params) => params.row.shippingInfo.fullName 
    },
    { 
      field: 'email', 
      headerName: 'Email', 
      width: 200,
      valueGetter: (params) => params.row.shippingInfo.email 
    },
    { 
      field: 'phone', 
      headerName: 'Điện thoại', 
      width: 150,
      valueGetter: (params) => params.row.shippingInfo.phone 
    },
    { 
      field: 'address', 
      headerName: 'Địa chỉ', 
      width: 250,
      valueGetter: (params) => params.row.shippingInfo.address 
    },
    { 
      field: 'city', 
      headerName: 'Thành phố', 
      width: 150,
      valueGetter: (params) => params.row.shippingInfo.city 
    },
    { 
      field: 'district', 
      headerName: 'Quận/Huyện', 
      width: 150,
      valueGetter: (params) => params.row.shippingInfo.district 
    },
    { 
      field: 'ward', 
      headerName: 'Phường/Xã', 
      width: 150,
      valueGetter: (params) => params.row.shippingInfo.ward 
    },
    {
      field: 'totalAmount',
      headerName: 'Tổng tiền',
      width: 130,
      valueFormatter: (params) => {
        return new Intl.NumberFormat('vi-VN', {
          style: 'currency',
          currency: 'VND'
        }).format(params.value);
      }
    },
    {
      field: 'status',
      headerName: 'Trạng thái',
      width: 130,
      valueFormatter: (params) => {
        const statusMap = {
          'pending': 'Chờ xác nhận',
          'confirmed': 'Đã xác nhận',
          'shipping': 'Đang giao',
          'delivered': 'Đã giao',
          'cancelled': 'Đã hủy'
        };
        return statusMap[params.value] || params.value;
      }
    },
    {
      field: 'paymentMethod',
      headerName: 'Thanh toán',
      width: 130,
      valueFormatter: (params) => {
        const methodMap = {
          'cod': 'Tiền mặt',
          'banking': 'Chuyển khoản',
          'vnpay': 'VNPay'
        };
        return methodMap[params.value] || params.value;
      }
    },
    {
      field: 'createdAt',
      headerName: 'Ngày tạo',
      width: 180,
      valueFormatter: (params) => {
        return new Date(params.value).toLocaleString('vi-VN');
      }
    },
    {
      field: 'actions',
      headerName: 'Thao tác',
      width: 130,
      renderCell: (params) => (
        <Box>
          {params.row.status === 'pending' && (
            <IconButton 
              color="primary" 
              onClick={() => onConfirm(params.row._id)}
              title="Xác nhận đơn hàng"
            >
              <CheckIcon />
            </IconButton>
          )}
          <IconButton 
            color="primary" 
            onClick={() => onEdit(params.row)}
            title="Sửa đơn hàng"
          >
            <EditIcon />
          </IconButton>
        </Box>
      )
    }
  ];

  return (
    <Box sx={{ height: 600, width: '100%' }}>
      <DataGrid
        rows={orders}
        columns={columns}
        pageSize={10}
        rowsPerPageOptions={[10]}
        getRowId={(row) => row._id}
        disableSelectionOnClick
      />
    </Box>
  );
};

export default OrderList;
