import { useState, useEffect } from 'react';
import { Box, Snackbar, Alert } from '@mui/material';
import axios from 'axios';
import OrderList from '../components/orders/OrderList';
import OrderForm from '../components/orders/OrderForm';

const OrdersPage = () => {
  const [orders, setOrders] = useState([]);
  const [error, setError] = useState('');
  const [snackbar, setSnackbar] = useState({ open: false, message: '', severity: 'success' });
  const [formData, setFormData] = useState({
    _id: '',
    shippingInfo: {
      fullName: '',
      email: '',
      phone: '',
      address: '',
      city: '',
      district: '',
      ward: '',
      note: ''
    },
    totalAmount: 0,
    status: '',
    paymentMethod: '',
    items: []
  });
  const [open, setOpen] = useState(false);

  const fetchOrders = async () => {
    try {
      const headers = {
        Authorization: `Bearer ${localStorage.getItem('token')}`
      };
      const response = await axios.get('http://localhost:5000/api/orders/admin/all', { headers });

      const formattedOrders = response.data.orders.map(order => ({
        _id: order._id,
        user: order.user,
        items: order.items.map(item => ({
          productId: item.productId._id,
          name: item.name,
          quantity: item.quantity,
          price: item.price,
          size: item.size
        })),
        shippingInfo: {
          fullName: order.shippingInfo.fullName,
          email: order.shippingInfo.email,
          phone: order.shippingInfo.phone,
          address: order.shippingInfo.address,
          city: order.shippingInfo.city,
          district: order.shippingInfo.district,
          ward: order.shippingInfo.ward,
          note: order.shippingInfo.note || ''
        },
        totalAmount: order.totalAmount,
        status: order.status,
        paymentMethod: order.paymentMethod,
        paymentStatus: order.paymentStatus,
        createdAt: order.createdAt,
        updatedAt: order.updatedAt
      }));

      setOrders(formattedOrders);
    } catch (error) {
      console.error('Error fetching orders:', error);
      setError('Không thể tải danh sách đơn hàng');
    }
  };

  useEffect(() => {
    fetchOrders();
  }, []);

  const handleConfirm = async (id) => {
  try {
    const headers = {
      Authorization: `Bearer ${localStorage.getItem('token')}`
    };
    await axios.put(`http://localhost:5000/api/orders/${id}/status`, { status: 'confirmed' }, { headers });
    setSnackbar({ open: true, message: 'Xác nhận đơn hàng thành công', severity: 'success' });
    fetchOrders();
  } catch (error) {
    console.error('Error confirming order:', error);
    const errorMessage = error.response?.data?.message || 'Có lỗi xảy ra khi xác nhận đơn hàng';
    setError(errorMessage);
    setSnackbar({ open: true, message: errorMessage, severity: 'error' });
  } finally {
    setError(''); // Reset error state after handling
  }
};

  const handleEditOrder = (order) => {
    setFormData({
      _id: order._id,
      shippingInfo: order.shippingInfo,
      totalAmount: order.totalAmount,
      status: order.status,
      paymentMethod: order.paymentMethod,
      items: order.items
    });
    setOpen(true);
  };

  return (
    <Box>
      <OrderList orders={orders} onConfirm={handleConfirm} />
      <OrderForm open={open} onClose={() => setOpen(false)} formData={formData} setFormData={setFormData} onSubmit={handleEditOrder} />
      <Snackbar open={!!error || snackbar.open} autoHideDuration={6000} onClose={() => setError('')}>
        <Alert severity={snackbar.severity || 'error'} onClose={() => setError('')}>
          {error || snackbar.message}
        </Alert>
      </Snackbar>
    </Box>
  );
};

export default OrdersPage;
