import { useState, useEffect } from 'react';
import { Box, Typography, Snackbar, Alert } from '@mui/material';
import axios from 'axios';
import OrderList from '../components/orders/OrderList';

const OrdersPage = () => {
  const [orders, setOrders] = useState([]);
  const [error, setError] = useState('');
  const [snackbar, setSnackbar] = useState({ open: false, message: '', severity: 'success' });

  const fetchOrders = async () => {
    try {
      const headers = {
        Authorization: `Bearer ${localStorage.getItem('token')}`
      };
      const response = await axios.get('http://localhost:5000/api/orders/admin/all', { headers });
      setOrders(response.data.orders);
    } catch (error) {
      console.error('Error fetching orders:', error);
      setError('Không thể tải danh sách đơn hàng');
      setSnackbar({ open: true, message: 'Không thể tải danh sách đơn hàng', severity: 'error' });
    }
  };

  useEffect(() => {
    fetchOrders();
  }, []);

  const handleConfirm = async (id) => {
    try {
      const headers = {
        Authorization: `Bearer ${localStorage.getItem('token')}`
      };
      await axios.put(`http://localhost:5000/api/orders/${id}/status`, 
        { status: 'confirmed' }, 
        { headers }
      );
      setSnackbar({ open: true, message: 'Xác nhận đơn hàng thành công', severity: 'success' });
      fetchOrders();
    } catch (error) {
      console.error('Error confirming order:', error);
      setSnackbar({ open: true, message: 'Có lỗi xảy ra khi xác nhận đơn hàng', severity: 'error' });
    }
  };

  const handleSnackbarClose = () => {
    setSnackbar({ ...snackbar, open: false });
    setError('');
  };

  return (
    <Box>
      <Typography variant="h5" sx={{ mb: 3 }}>
        Quản lý đơn hàng
      </Typography>

      <OrderList 
        orders={orders} 
        onConfirm={handleConfirm}
      />

      <Snackbar 
        open={snackbar.open} 
        autoHideDuration={6000} 
        onClose={handleSnackbarClose}
      >
        <Alert 
          onClose={handleSnackbarClose} 
          severity={snackbar.severity}
          sx={{ width: '100%' }}
        >
          {snackbar.message || error}
        </Alert>
      </Snackbar>
    </Box>
  );
};

export default OrdersPage;
